<script>
    function arrayselect() {
        var statusArr = document.querySelectorAll(""[id^='Check_'"");
        var checkedid = [];
        for (i = 0; i < statusArr.length; i++) {
            if (statusArr[i].checked == true) {
                var id = statusArr[i].id.slice(6)
                checkedid.push(id);
            }
        }
        return checkedid;
    }
